package external

import (
	"github.com/Grivn/phalanx/pkg/common/protos"
)

// Sender has provided the service for network communication.
type Sender interface {
	// BroadcastCommand is used to send the commands generated by proposer nodes.
	BroadcastCommand(command *protos.Command)

	// BroadcastPCM is used to send the message to every node in current cluster.
	BroadcastPCM(message *protos.ConsensusMessage)

	// UnicastPCM is used to send the message to the target node.
	UnicastPCM(message *protos.ConsensusMessage)
}
